import type { DoubleEliminationBracket, Match } from "./bracket-types";
import { MatchCard } from "./match-card";
import { cn } from "./cn";

export interface DoubleEliminationProps {
  bracket: DoubleEliminationBracket;
  className?: string;
  onMatchClick?: (match: Match) => void;
}

export function DoubleElimination({ bracket, className, onMatchClick }: DoubleEliminationProps) {
  const { upper, lower, grandFinal } = bracket;

  const ratio = lower.length > upper.length + (grandFinal ? 1 : 0) ? 2 : 1;

  const maxLBMatches = Math.max(...lower.map((r) => r.matches.length));
  const lbExps = lower.map((r) => maxLBMatches / r.matches.length);

  const lbConnectors: Array<"merge" | "straight"> = [];
  for (let i = 0; i < lower.length - 1; i++) {
    lbConnectors.push(
      lower[i + 1]!.matches.length < lower[i]!.matches.length ? "merge" : "straight",
    );
  }

  const connectorWidth =
    ratio === 2
      ? "calc(var(--bracket-match-width, 11rem) + 2 * var(--bracket-round-gap, 3rem))"
      : "var(--bracket-round-gap, 3rem)";

  const ubLastExp = Math.pow(2, upper.length - 1);
  const lbLastExp = lbExps[lbExps.length - 1]!;

  const ubRow = (
    <div className="inline-flex">
      {upper.map((round, i) => {
        const isLast = i === upper.length - 1;
        const exp = Math.pow(2, i);
        return (
          <div key={round.name} className="flex">
            <RoundColumn
              name={round.name}
              matches={round.matches}
              exp={exp}
              onMatchClick={onMatchClick}
            />
            {!isLast && (
              <MergeConnectorColumn
                exp={exp}
                pairCount={Math.floor(round.matches.length / 2)}
                width={connectorWidth}
              />
            )}
          </div>
        );
      })}
      {grandFinal && (
        <StraightConnectorColumn
          exp={ubLastExp}
          count={1}
          width="calc(var(--bracket-match-width, 11rem) + var(--bracket-round-gap, 3rem))"
        />
      )}
    </div>
  );

  const lbRow = (
    <div className="inline-flex">
      {lower.map((round, i) => {
        const isLast = i === lower.length - 1;
        const exp = lbExps[i]!;
        return (
          <div key={round.name} className="flex">
            <RoundColumn
              name={round.name}
              matches={round.matches}
              exp={exp}
              onMatchClick={onMatchClick}
            />
            {!isLast &&
              (lbConnectors[i] === "merge" ? (
                <MergeConnectorColumn exp={exp} pairCount={Math.floor(round.matches.length / 2)} />
              ) : (
                <StraightConnectorColumn exp={exp} count={round.matches.length} />
              ))}
          </div>
        );
      })}
    </div>
  );

  if (grandFinal) {
    return (
      <div
        className={cn("inline-grid overflow-x-auto", "rounded-lg p-6", "bg-background", className)}
        style={
          {
            gridTemplateRows: "auto auto",
            gridTemplateColumns: "auto auto auto",
          } as React.CSSProperties
        }
      >
        <section style={{ gridRow: 1, gridColumn: 1 }}>{ubRow}</section>
        <section style={{ gridRow: 2, gridColumn: 1 }}>{lbRow}</section>

        {/* GF connector — unified column spanning both rows */}
        <GrandFinalConnectorColumn
          gridRow="1 / 3"
          gridColumn={2}
          ubExp={ubLastExp}
          lbExp={lbLastExp}
        />

        {/* GF card spanning both rows — same header+flex-1 structure as connector */}
        <div
          className="flex flex-col items-center shrink-0"
          style={
            {
              gridRow: "1 / 3",
              gridColumn: 3,
            } as React.CSSProperties
          }
        >
          <div className="text-xs font-medium text-muted-foreground mb-3 whitespace-nowrap">
            Grand Final
          </div>
          <div className="flex-1 flex items-center">
            <div
              style={
                {
                  height: "var(--bracket-match-height, calc(3.25rem + 1px))",
                } as React.CSSProperties
              }
            >
              <MatchCard match={grandFinal} onMatchClick={onMatchClick} className="h-full" />
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div
      className={cn(
        "inline-flex flex-col gap-8 overflow-x-auto",
        "rounded-lg p-6",
        "bg-background",
        className,
      )}
    >
      <section>{ubRow}</section>
      <section>{lbRow}</section>
    </div>
  );
}

function RoundColumn({
  name,
  matches,
  exp,
  onMatchClick,
}: {
  name: string;
  matches: Match[];
  exp: number;
  onMatchClick?: (match: Match) => void;
}) {
  return (
    <div className="flex flex-col items-center shrink-0">
      <div className="text-xs font-medium text-muted-foreground mb-3 whitespace-nowrap">{name}</div>
      <div
        className="flex flex-col"
        style={
          {
            "--_base":
              "calc(var(--bracket-match-height, calc(3.25rem + 1px)) + var(--bracket-match-gap, 1rem))",
            "--_slot": `calc(${exp} * var(--_base))`,
          } as React.CSSProperties
        }
      >
        {matches.map((match) => (
          <div
            key={match.id}
            className="flex items-center"
            style={{ height: "var(--_slot)" } as React.CSSProperties}
          >
            <div
              style={
                {
                  height: "var(--bracket-match-height, calc(3.25rem + 1px))",
                } as React.CSSProperties
              }
            >
              <MatchCard match={match} onMatchClick={onMatchClick} className="h-full" />
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

function MergeConnectorColumn({
  exp,
  pairCount,
  width,
}: {
  exp: number;
  pairCount: number;
  width?: string;
}) {
  return (
    <div className="flex flex-col shrink-0">
      <div className="text-xs mb-3 invisible" aria-hidden="true">
        &nbsp;
      </div>
      <div
        className="flex flex-col"
        style={
          {
            "--_base":
              "calc(var(--bracket-match-height, calc(3.25rem + 1px)) + var(--bracket-match-gap, 1rem))",
            "--_pair-h": `calc(${exp * 2} * var(--_base))`,
            width: width ?? "var(--bracket-round-gap, 3rem)",
          } as React.CSSProperties
        }
      >
        {Array.from({ length: pairCount }, (_, i) => (
          <svg
            key={i}
            className="w-full text-border"
            style={{ height: "var(--_pair-h)" }}
            viewBox="0 0 100 100"
            preserveAspectRatio="none"
            aria-hidden
          >
            <path
              d={["M 0 25 H 50", "M 0 75 H 50", "M 50 25 V 75", "M 50 50 H 100"].join(" ")}
              fill="none"
              stroke="currentColor"
              strokeWidth={1.5}
              vectorEffect="non-scaling-stroke"
            />
          </svg>
        ))}
      </div>
    </div>
  );
}

function GrandFinalConnectorColumn({
  gridRow,
  gridColumn,
  ubExp,
  lbExp,
}: {
  gridRow: string;
  gridColumn: number;
  ubExp: number;
  lbExp: number;
}) {
  return (
    <div
      className="flex flex-col w-full h-full"
      style={
        {
          gridRow,
          gridColumn,
          width: "var(--bracket-round-gap, 3rem)",
        } as React.CSSProperties
      }
    >
      {/* Spacer to match RoundColumn header height */}
      <div className="text-xs mb-3 invisible" aria-hidden="true">
        &nbsp;
      </div>

      <div
        className="relative w-full flex-1"
        style={
          {
            "--_base":
              "calc(var(--bracket-match-height, calc(3.25rem + 1px)) + var(--bracket-match-gap, 1rem))",
            "--_ub-h": `calc(${ubExp} * var(--_base))`,
            "--_lb-h": `calc(${lbExp} * var(--_base))`,
          } as React.CSSProperties
        }
      >
        {/* Upper Arm: Start at UB Center, go Down to GF Match Center */}
        <div
          className="absolute left-0 w-1/2 border-border"
          style={{
            top: "calc(var(--_ub-h) / 2 - 1px)",
            height: "calc(50% - var(--_ub-h) / 2 + 1px)",
            borderTopWidth: "2px",
            borderRightWidth: "2px",
          }}
        />

        {/* Lower Arm: Start at LB Center, go Up to GF Match Center */}
        <div
          className="absolute left-0 w-1/2 border-border"
          style={{
            bottom: "calc(var(--_lb-h) / 2 - 1px)",
            height: "calc(50% - var(--_lb-h) / 2 + 1px)",
            borderBottomWidth: "2px",
            borderRightWidth: "2px",
          }}
        />

        {/* Final Leg: Horizontal to right */}
        <div
          className="absolute right-0 w-1/2 border-border"
          style={{
            top: "calc(50% - 1px)",
            borderTopWidth: "2px",
          }}
        />
      </div>
    </div>
  );
}

function StraightConnectorColumn({
  exp,
  count,
  width,
}: {
  exp: number;
  count: number;
  width?: string;
}) {
  return (
    <div className="flex flex-col shrink-0">
      <div className="text-xs mb-3 invisible" aria-hidden="true">
        &nbsp;
      </div>
      <div
        className="flex flex-col"
        style={
          {
            "--_base":
              "calc(var(--bracket-match-height, calc(3.25rem + 1px)) + var(--bracket-match-gap, 1rem))",
            "--_slot-h": `calc(${exp} * var(--_base))`,
            width: width ?? "var(--bracket-round-gap, 3rem)",
          } as React.CSSProperties
        }
      >
        {Array.from({ length: count }, (_, i) => (
          <svg
            key={i}
            className="w-full text-border"
            style={{ height: "var(--_slot-h)" }}
            viewBox="0 0 100 100"
            preserveAspectRatio="none"
            aria-hidden
          >
            <path
              d="M 0 50 H 100"
              fill="none"
              stroke="currentColor"
              strokeWidth={1.5}
              vectorEffect="non-scaling-stroke"
            />
          </svg>
        ))}
      </div>
    </div>
  );
}

import type { SingleEliminationBracket, Match } from "./bracket-types";
import { MatchCard } from "./match-card";
import { cn } from "./cn";

export interface SingleEliminationProps {
  bracket: SingleEliminationBracket;
  className?: string;
  onMatchClick?: (match: Match) => void;
}

export function SingleElimination({ bracket, className, onMatchClick }: SingleEliminationProps) {
  const rounds = bracket.rounds;

  return (
    <div
      className={cn("inline-flex overflow-x-auto", "rounded-lg p-6", "bg-background", className)}
    >
      {rounds.map((round, roundIdx) => {
        const isLast = roundIdx === rounds.length - 1;
        return (
          <div key={round.name} className="flex">
            <RoundColumn
              roundIdx={roundIdx}
              name={round.name}
              matches={round.matches}
              onMatchClick={onMatchClick}
            />
            {!isLast && <ConnectorColumn roundIdx={roundIdx} sourceCount={round.matches.length} />}
          </div>
        );
      })}
    </div>
  );
}

function RoundColumn({
  roundIdx,
  name,
  matches,
  onMatchClick,
}: {
  roundIdx: number;
  name: string;
  matches: Match[];
  onMatchClick?: (match: Match) => void;
}) {
  const exp = Math.pow(2, roundIdx);

  return (
    <div className="flex flex-col items-center shrink-0">
      <div className="text-xs font-medium text-muted-foreground mb-3 whitespace-nowrap">{name}</div>
      <div
        className="flex flex-col"
        style={
          {
            "--_base":
              "calc(var(--bracket-match-height, calc(3.25rem + 1px)) + var(--bracket-match-gap, 1rem))",
            "--_slot": `calc(${exp} * var(--_base))`,
          } as React.CSSProperties
        }
      >
        {matches.map((match) => (
          <div
            key={match.id}
            className="flex items-center"
            style={
              {
                height: "var(--_slot)",
              } as React.CSSProperties
            }
          >
            <div
              style={
                {
                  height: "var(--bracket-match-height, calc(3.25rem + 1px))",
                } as React.CSSProperties
              }
            >
              <MatchCard match={match} onMatchClick={onMatchClick} className="h-full" />
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

function ConnectorColumn({ roundIdx, sourceCount }: { roundIdx: number; sourceCount: number }) {
  const pairCount = Math.floor(sourceCount / 2);
  const exp = Math.pow(2, roundIdx);

  return (
    <div className="flex flex-col shrink-0">
      {/* Invisible spacer matching the round name height */}
      <div className="text-xs mb-3 invisible" aria-hidden="true">
        &nbsp;
      </div>
      <div
        className="flex flex-col"
        style={
          {
            "--_base":
              "calc(var(--bracket-match-height, calc(3.25rem + 1px)) + var(--bracket-match-gap, 1rem))",
            "--_pair-h": `calc(${exp * 2} * var(--_base))`,
            width: "var(--bracket-round-gap, 3rem)",
          } as React.CSSProperties
        }
      >
        {Array.from({ length: pairCount }, (_, i) => (
          <svg
            key={i}
            className="w-full text-border"
            style={{ height: `var(--_pair-h)` }}
            viewBox="0 0 100 100"
            preserveAspectRatio="none"
            aria-hidden
          >
            <path
              d={["M 0 25 H 50", "M 0 75 H 50", "M 50 25 V 75", "M 50 50 H 100"].join(" ")}
              fill="none"
              stroke="currentColor"
              strokeWidth={1.5}
              vectorEffect="non-scaling-stroke"
            />
          </svg>
        ))}
      </div>
    </div>
  );
}

import type { Match, MatchTeam } from "./bracket-types";
import { cn } from "./cn";

const WEEKDAYS = ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"];
const MONTHS = ["Jan", "Feb", "Mar", "Apr", "May", "Jun", "Jul", "Aug", "Sep", "Oct", "Nov", "Dec"];

function formatSchedule(value: Date | string): { time: string; day: string } {
  const date = value instanceof Date ? value : new Date(value);
  if (Number.isNaN(date.getTime())) return { time: "", day: String(value) };

  const now = new Date();
  const today = new Date(now.getFullYear(), now.getMonth(), now.getDate());
  const target = new Date(date.getFullYear(), date.getMonth(), date.getDate());
  const diffDays = Math.round((target.getTime() - today.getTime()) / 86_400_000);

  const time = `${String(date.getHours()).padStart(2, "0")}:${String(date.getMinutes()).padStart(2, "0")}`;

  if (diffDays === 0) return { time, day: "Today" };
  if (diffDays === 1) return { time, day: "Tomorrow" };
  if (diffDays > 1 && diffDays < 7) return { time, day: WEEKDAYS[date.getDay()]! };
  return { time, day: `${MONTHS[date.getMonth()]} ${date.getDate()}` };
}

export interface MatchCardProps {
  match: Match;
  className?: string;
  onMatchClick?: (match: Match) => void;
}

function TeamRow({
  entry,
  isLive,
  isLeading,
}: {
  entry: MatchTeam;
  isLive?: boolean;
  isLeading?: boolean;
}) {
  const isWinner = entry.isWinner === true;
  const isLoser = entry.isWinner === false;

  return (
    <div
      className={cn(
        "flex items-center gap-2 px-2.5 py-1.5",
        "border-l-3",
        isWinner && "border-l-emerald-500",
        isLoser && "border-l-destructive",
        !isWinner && !isLoser && "border-l-transparent",
      )}
    >
      <div className="flex items-center gap-2 min-w-0 flex-1">
        {entry.team?.logo && (
          <img
            src={entry.team.logo}
            alt={entry.team.name}
            className="h-4 w-4 shrink-0 object-contain"
          />
        )}
        <span
          className={cn(
            "truncate text-sm leading-none",
            isWinner && "font-semibold text-card-foreground",
            isLoser && "text-card-foreground/40",
            !isWinner && !isLoser && "text-card-foreground",
            !entry.team && "text-muted-foreground",
          )}
        >
          {entry.team?.name ?? "TBD"}
        </span>
      </div>
      <span
        className={cn(
          "text-sm tabular-nums shrink-0 leading-none font-medium",
          isLive && isLeading && "text-emerald-500",
          isLive && isLeading === false && "text-destructive",
          !isLive && isWinner && "text-card-foreground",
          !isLive && isLoser && "text-card-foreground/40",
        )}
      >
        {entry.score}
      </span>
    </div>
  );
}

export function MatchCard({ match, className, onMatchClick }: MatchCardProps) {
  const bestOfLabel = match.bestOf ? `BO${match.bestOf}` : undefined;
  const isLive = match.status === "live";
  const isCompleted = match.status === "completed";
  const hasHeader = !isCompleted && (isLive || match.scheduledAt || bestOfLabel);

  return (
    <div
      className={cn(
        "w-(--bracket-match-width,11rem) rounded-sm shadow-sm",
        "border-border",
        "bg-card",
        "text-card-foreground",
        "shadow-sm overflow-hidden",
        isLive && "border-destructive border",
        onMatchClick && "cursor-pointer hover:border-primary/50 transition-colors",
        className,
      )}
      onClick={onMatchClick ? () => onMatchClick(match) : undefined}
    >
      {hasHeader && (
        <div
          className={`flex items-center justify-between px-2.5 py-1 text-xs text-muted-foreground bg-muted ${isLive && "text-primary bg-destructive!"}`}
        >
          <span className="flex items-center gap-1.5">
            {isLive && (
              <>
                <span className="relative flex h-2 w-2">
                  <span className="absolute inline-flex h-full w-full animate-ping rounded-full bg-primary opacity-75" />
                  <span className="relative inline-flex h-2 w-2 rounded-full bg-primary" />
                </span>
                <span className="font-semibold uppercase">Live</span>
              </>
            )}
            {!isLive &&
              match.scheduledAt &&
              (() => {
                const schedule = formatSchedule(match.scheduledAt!);
                return (
                  <span className="flex items-center gap-1">
                    <span className="font-bold text-[10px] mt-[1px]">{schedule.time}</span>
                    <span>{schedule.day}</span>
                  </span>
                );
              })()}
            {!isLive && !match.scheduledAt && <span>Unscheduled</span>}
          </span>
          {bestOfLabel && (
            <span className={`ml-auto ${isLive && "font-semibold"}`}>{bestOfLabel}</span>
          )}
        </div>
      )}
      <TeamRow
        entry={match.teams[0]}
        isLive={isLive}
        isLeading={
          isLive
            ? match.teams[0].score > match.teams[1].score
              ? true
              : match.teams[0].score < match.teams[1].score
                ? false
                : undefined
            : undefined
        }
      />
      <div className="border-t border-border" />
      <TeamRow
        entry={match.teams[1]}
        isLive={isLive}
        isLeading={
          isLive
            ? match.teams[1].score > match.teams[0].score
              ? true
              : match.teams[1].score < match.teams[0].score
                ? false
                : undefined
            : undefined
        }
      />
    </div>
  );
}

export interface Team {
  id: string;
  name: string;
  logo?: string;
  seed?: number;
}

export interface MatchTeam {
  team: Team | null;
  score: number;
  isWinner?: boolean;
}

export interface Match {
  id: string;
  round: number;
  position: number;
  bestOf?: number;
  scheduledAt?: Date | string;
  status?: MatchStatus;
  teams: [MatchTeam, MatchTeam];
}

export type MatchStatus = "upcoming" | "live" | "completed";

export interface Round {
  name: string;
  matches: Match[];
}

export interface SingleEliminationBracket {
  type: "single-elimination";
  rounds: Round[];
}

export interface DoubleEliminationBracket {
  type: "double-elimination";
  upper: Round[];
  lower: Round[];
  grandFinal?: Match;
}

export interface SwissStanding {
  team: Team;
  wins: number;
  losses: number;
  tiebreaker?: number;
  status?: "advancing" | "eliminated" | "pending";
}

export interface SwissRound {
  name: string;
  record?: string;
  matches: Match[];
}

export interface SwissBracket {
  type: "swiss";
  rounds: SwissRound[];
  standings: SwissStanding[];
  winsToAdvance: number;
  lossesToEliminate: number;
}

export interface GroupStanding {
  team: Team;
  wins: number;
  losses: number;
  draws: number;
  points: number;
  differential: number;
}

export interface Group {
  name: string;
  teams: Team[];
  matches: Match[];
  standings: GroupStanding[];
}

export interface GroupStageBracket {
  type: "group-stage";
  groups: Group[];
}

export type Bracket =
  | SingleEliminationBracket
  | DoubleEliminationBracket
  | SwissBracket
  | GroupStageBracket;

import { type ClassValue, clsx } from "clsx";
import { twMerge } from "tailwind-merge";

export function cn(...inputs: ClassValue[]) {
  return twMerge(clsx(inputs));
}
