import type { GroupStageBracket, Match, Group } from "./bracket-types";
import { MatchCard } from "./match-card";
import { cn } from "./cn";
import {
  Table,
  TableHeader,
  TableBody,
  TableRow,
  TableHead,
  TableCell,
} from "./table";

export interface GroupStageProps {
  bracket: GroupStageBracket;
  className?: string;
  onMatchClick?: (match: Match) => void;
}

function GroupTable({ group }: { group: Group }) {
  return (
    <div className="rounded border border-border bg-card">
      <h3 className="text-sm font-medium px-3 py-2 border-b border-border">{group.name}</h3>
      <Table>
        <TableHeader>
          <TableRow className="text-muted-foreground">
            <TableHead>#</TableHead>
            <TableHead>Team</TableHead>
            <TableHead className="text-center">W</TableHead>
            <TableHead className="text-center">D</TableHead>
            <TableHead className="text-center">L</TableHead>
            <TableHead className="text-center">+/-</TableHead>
            <TableHead className="text-center">Pts</TableHead>
          </TableRow>
        </TableHeader>
        <TableBody>
          {group.standings.map((s, i) => (
            <TableRow key={s.team.id}>
              <TableCell className="text-muted-foreground">{i + 1}</TableCell>
              <TableCell className="flex items-center gap-2">
                {s.team.logo && (
                  <img src={s.team.logo} alt={s.team.name} className="h-4 w-4 object-contain" />
                )}
                {s.team.name}
              </TableCell>
              <TableCell className="text-center tabular-nums">{s.wins}</TableCell>
              <TableCell className="text-center tabular-nums">{s.draws}</TableCell>
              <TableCell className="text-center tabular-nums">{s.losses}</TableCell>
              <TableCell className="text-center tabular-nums">
                {s.differential > 0 ? `+${s.differential}` : s.differential}
              </TableCell>
              <TableCell className="text-center tabular-nums font-medium">{s.points}</TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </div>
  );
}

function GroupMatches({
  group,
  onMatchClick,
}: {
  group: Group;
  onMatchClick?: (match: Match) => void;
}) {
  return (
    <div className="flex flex-col gap-4">
      <h4 className="text-sm font-medium text-muted-foreground">{group.name} â€” Matches</h4>
      <div className="flex flex-wrap gap-4">
        {group.matches.map((match) => (
          <MatchCard key={match.id} match={match} onMatchClick={onMatchClick} />
        ))}
      </div>
    </div>
  );
}

export function GroupStage({ bracket, className, onMatchClick }: GroupStageProps) {
  return (
    <div className={cn("flex flex-col gap-8 p-4", className)}>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {bracket.groups.map((group) => (
          <GroupTable key={group.name} group={group} />
        ))}
      </div>

      <div className="flex flex-col gap-6">
        {bracket.groups.map((group) => (
          <GroupMatches key={group.name} group={group} onMatchClick={onMatchClick} />
        ))}
      </div>
    </div>
  );
}

import type { Match, MatchTeam } from "./bracket-types";
import { cn } from "./cn";

const WEEKDAYS = ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"];
const MONTHS = ["Jan", "Feb", "Mar", "Apr", "May", "Jun", "Jul", "Aug", "Sep", "Oct", "Nov", "Dec"];

function formatSchedule(value: Date | string): { time: string; day: string } {
  const date = value instanceof Date ? value : new Date(value);
  if (Number.isNaN(date.getTime())) return { time: "", day: String(value) };

  const now = new Date();
  const today = new Date(now.getFullYear(), now.getMonth(), now.getDate());
  const target = new Date(date.getFullYear(), date.getMonth(), date.getDate());
  const diffDays = Math.round((target.getTime() - today.getTime()) / 86_400_000);

  const time = `${String(date.getHours()).padStart(2, "0")}:${String(date.getMinutes()).padStart(2, "0")}`;

  if (diffDays === 0) return { time, day: "Today" };
  if (diffDays === 1) return { time, day: "Tomorrow" };
  if (diffDays > 1 && diffDays < 7) return { time, day: WEEKDAYS[date.getDay()]! };
  return { time, day: `${MONTHS[date.getMonth()]} ${date.getDate()}` };
}

export interface MatchCardProps {
  match: Match;
  className?: string;
  onMatchClick?: (match: Match) => void;
  variant?: "default" | "bordered";
}

function TeamRow({
  entry,
  isLive,
  isLeading,
  variant = "default",
}: {
  entry: MatchTeam;
  isLive?: boolean;
  isLeading?: boolean;
  variant?: "default" | "bordered";
}) {
  const isWinner = entry.isWinner === true;
  const isLoser = entry.isWinner === false;
  const showBorders = variant === "bordered";

  return (
    <div
      className={cn(
        "flex items-center gap-2 px-2.5 py-1.5",
        showBorders && "border-l-3",
        showBorders && isWinner && "border-l-emerald-500",
        showBorders && isLoser && "border-l-destructive",
        showBorders && !isWinner && !isLoser && "border-l-transparent",
      )}
    >
      <div className="flex items-center gap-2 min-w-0 flex-1">
        {entry.team?.logo && (
          <img
            src={entry.team.logo}
            alt={entry.team.name}
            className="h-4 w-4 shrink-0 object-contain"
          />
        )}
        <span
          className={cn(
            "truncate text-sm leading-none",
            isWinner && "font-semibold text-card-foreground",
            isLoser && "text-card-foreground/40",
            !isWinner && !isLoser && "text-card-foreground",
            !entry.team && "text-muted-foreground",
          )}
        >
          {entry.team?.name ?? "TBD"}
        </span>
      </div>
      <span
        className={cn(
          "text-sm tabular-nums shrink-0 leading-none font-medium",
          isLive && isLeading && "text-emerald-500",
          isLive && isLeading === false && "text-destructive",
          !isLive && isWinner && "text-card-foreground",
          !isLive && isLoser && "text-card-foreground/40",
        )}
      >
        {entry.score}
      </span>
    </div>
  );
}

export function MatchCard({ match, className, onMatchClick, variant = "default" }: MatchCardProps) {
  const bestOfLabel = match.bestOf ? `BO${match.bestOf}` : undefined;
  const isLive = match.status === "live";
  const isCompleted = match.status === "completed";
  const hasHeader = !isCompleted && (isLive || match.scheduledAt || bestOfLabel);

  return (
    <div
      className={cn(
        "w-(--bracket-match-width,11rem) rounded-sm shadow-sm",
        "border-border",
        "bg-card",
        "text-card-foreground",
        "shadow-sm overflow-hidden",
        isLive && "border-destructive border",
        onMatchClick && "cursor-pointer hover:border-primary/50 transition-colors",
        className,
      )}
      onClick={onMatchClick ? () => onMatchClick(match) : undefined}
    >
      {hasHeader && (
        <div
          className={`flex items-center justify-between px-2.5 py-1 text-xs text-muted-foreground bg-muted ${isLive && "text-primary bg-destructive!"}`}
        >
          <span className="flex items-center gap-1.5">
            {isLive && (
              <>
                <span className="relative flex h-2 w-2">
                  <span className="absolute inline-flex h-full w-full animate-ping rounded-full bg-primary opacity-75" />
                  <span className="relative inline-flex h-2 w-2 rounded-full bg-primary" />
                </span>
                <span className="font-semibold uppercase">Live</span>
              </>
            )}
            {!isLive &&
              match.scheduledAt &&
              (() => {
                const schedule = formatSchedule(match.scheduledAt!);
                return (
                  <span className="flex items-center gap-1">
                    <span className="font-bold text-[10px] mt-[1px]">{schedule.time}</span>
                    <span>{schedule.day}</span>
                  </span>
                );
              })()}
            {!isLive && !match.scheduledAt && <span>Unscheduled</span>}
          </span>
          {bestOfLabel && (
            <span className={`ml-auto ${isLive && "font-semibold"}`}>{bestOfLabel}</span>
          )}
        </div>
      )}
      <TeamRow
        entry={match.teams[0]}
        isLive={isLive}
        variant={isCompleted ? variant : "default"}
        isLeading={
          isLive
            ? match.teams[0].score > match.teams[1].score
              ? true
              : match.teams[0].score < match.teams[1].score
                ? false
                : undefined
            : undefined
        }
      />
      <div className="border-t border-border" />
      <TeamRow
        entry={match.teams[1]}
        isLive={isLive}
        variant={isCompleted ? variant : "default"}
        isLeading={
          isLive
            ? match.teams[1].score > match.teams[0].score
              ? true
              : match.teams[1].score < match.teams[0].score
                ? false
                : undefined
            : undefined
        }
      />
    </div>
  );
}

import { cn } from "./cn";

function Table({ className, ...props }: React.ComponentProps<"table">) {
  return (
    <div data-slot="table-container" className="relative w-full overflow-x-auto">
      <table
        data-slot="table"
        className={cn("w-full caption-bottom text-sm", className)}
        {...props}
      />
    </div>
  );
}

function TableHeader({ className, ...props }: React.ComponentProps<"thead">) {
  return (
    <thead
      data-slot="table-header"
      className={cn("[&_tr]:border-b", className)}
      {...props}
    />
  );
}

function TableBody({ className, ...props }: React.ComponentProps<"tbody">) {
  return (
    <tbody
      data-slot="table-body"
      className={cn("[&_tr:last-child]:border-0", className)}
      {...props}
    />
  );
}

function TableRow({ className, ...props }: React.ComponentProps<"tr">) {
  return (
    <tr
      data-slot="table-row"
      className={cn(
        "hover:bg-muted/50 data-[state=selected]:bg-muted border-b transition-colors",
        className,
      )}
      {...props}
    />
  );
}

function TableHead({ className, ...props }: React.ComponentProps<"th">) {
  return (
    <th
      data-slot="table-head"
      className={cn(
        "text-foreground h-10 px-2 text-left align-middle font-medium whitespace-nowrap [&:has([role=checkbox])]:pr-0",
        className,
      )}
      {...props}
    />
  );
}

function TableCell({ className, ...props }: React.ComponentProps<"td">) {
  return (
    <td
      data-slot="table-cell"
      className={cn(
        "p-2 align-middle whitespace-nowrap [&:has([role=checkbox])]:pr-0",
        className,
      )}
      {...props}
    />
  );
}

export { Table, TableHeader, TableBody, TableRow, TableHead, TableCell };

export interface Team {
  id: string;
  name: string;
  logo?: string;
  seed?: number;
}

export interface MatchTeam {
  team: Team | null;
  score: number;
  isWinner?: boolean;
}

export interface Match {
  id: string;
  round: number;
  position: number;
  bestOf?: number;
  scheduledAt?: Date | string;
  status?: MatchStatus;
  teams: [MatchTeam, MatchTeam];
}

export type MatchStatus = "upcoming" | "live" | "completed";

export interface Round {
  name: string;
  matches: Match[];
}

export interface SingleEliminationBracket {
  type: "single-elimination";
  rounds: Round[];
}

export interface DoubleEliminationBracket {
  type: "double-elimination";
  upper: Round[];
  lower: Round[];
  grandFinal?: Match;
}

export interface SwissStanding {
  team: Team;
  wins: number;
  losses: number;
  tiebreaker?: number;
  status?: "advancing" | "eliminated" | "pending";
}

export interface SwissRound {
  name: string;
  record?: string;
  matches: Match[];
}

export interface SwissBracket {
  type: "swiss";
  rounds: SwissRound[];
  standings: SwissStanding[];
  winsToAdvance: number;
  lossesToEliminate: number;
}

export interface GroupStanding {
  team: Team;
  wins: number;
  losses: number;
  draws: number;
  points: number;
  differential: number;
}

export interface Group {
  name: string;
  teams: Team[];
  matches: Match[];
  standings: GroupStanding[];
}

export interface GroupStageBracket {
  type: "group-stage";
  groups: Group[];
}

export type Bracket =
  | SingleEliminationBracket
  | DoubleEliminationBracket
  | SwissBracket
  | GroupStageBracket;

import { type ClassValue, clsx } from "clsx";
import { twMerge } from "tailwind-merge";

export function cn(...inputs: ClassValue[]) {
  return twMerge(clsx(inputs));
}
